  return (
    <div className="chart-card">
      <div className="chart-header">
        <h3 className="chart-title">ðŸ“± Device Analytics</h3>
        <div className="chart-options">
          {periods.map((label) => (
            <span
              key={label}
              className={`chart-option ${
                activePeriod === label ? "active" : ""
              }`}
              onClick={() => onChangePeriod(label)}
            >
              {label}
            </span>
          ))}
        </div>
      </div>
