import React, { useEffect, useMemo, useRef, useState } from "react";

const normalize = (v) => (v ?? "").toString().toLowerCase();

export default function SearchableSelect({
  label,
  value,
  onChange,
  options = [],
  placeholder = "Select...",
  allowAll = true,
  searchable = true,

  // NEW
  multiple = false,
}) {
  const wrapRef = useRef(null);
  const inputRef = useRef(null);

  const [open, setOpen] = useState(false);
  const [query, setQuery] = useState("");

  const opts = useMemo(() => {
    // supports strings or {value,label}
    return (options || []).map((o) => {
      if (o && typeof o === "object") {
        const v = (o.value ?? o.label ?? "").toString();
        const l = (o.label ?? o.value ?? "").toString();
        return { value: v, label: l };
      }
      return { value: (o ?? "").toString(), label: (o ?? "").toString() };
    });
  }, [options]);

  const selectedValues = useMemo(() => {
    if (multiple) return Array.isArray(value) ? value.map(String) : [];
    return value ? [String(value)] : [];
  }, [value, multiple]);

  const selectedLabel = useMemo(() => {
    if (multiple) {
      if (!selectedValues.length) return "";
      // show "3 selected" style to keep UI compact
      if (selectedValues.length === 1) {
        const one = selectedValues[0];
        const found = opts.find((x) => x.value === one);
        return found?.label ?? one;
      }
      return `${selectedValues.length} selected`;
    }
    const v = selectedValues[0];
    if (!v) return "";
    const found = opts.find((x) => x.value === v);
    return found?.label ?? v;
  }, [multiple, selectedValues, opts]);

  const filtered = useMemo(() => {
    const q = normalize(query).trim();
    if (!q) return opts;
    return opts.filter((o) => normalize(o.label).includes(q) || normalize(o.value).includes(q));
  }, [opts, query]);

  const close = () => {
    setOpen(false);
    setQuery("");
  };

  useEffect(() => {
    const onDoc = (e) => {
      if (!wrapRef.current) return;
      if (!wrapRef.current.contains(e.target)) close();
    };
    document.addEventListener("mousedown", onDoc);
    return () => document.removeEventListener("mousedown", onDoc);
  }, []);

  const isSelected = (val) => selectedValues.includes(String(val));

  const pickSingle = (val) => {
    onChange?.(String(val));
    close();
  };

  const toggleMulti = (val) => {
    const s = String(val);
    const next = isSelected(s)
      ? selectedValues.filter((x) => x !== s)
      : [...selectedValues, s];
    onChange?.(next);
    // keep open for multi
    if (inputRef.current) inputRef.current.focus();
  };

  const clearAll = () => {
    onChange?.(multiple ? [] : "");
    close();
  };

  return (
    <div className="ss-field" ref={wrapRef}>
      {label && <label className="ss-label">{label}</label>}

      <div
        className="ss-control"
        onMouseDown={(e) => {
          // prevent blur/focus jitter
          e.preventDefault();
          setOpen(true);
          setTimeout(() => inputRef.current?.focus(), 0);
        }}
      >
        <input
          ref={inputRef}
          className="ss-input"
          value={open && searchable ? query : selectedLabel}
          placeholder={selectedLabel ? "" : placeholder}
          readOnly={!searchable}
          onChange={(e) => setQuery(e.target.value)}
          onFocus={() => setOpen(true)}
        />
        <span className="ss-caret">▾</span>
      </div>

      {open && (
        <div className="ss-menu">
          {allowAll && (
            <button type="button" className="ss-option" onMouseDown={(e) => e.preventDefault()} onClick={clearAll}>
              {multiple ? "Clear selection" : "All"}
            </button>
          )}

          {filtered.length === 0 ? (
            <div className="ss-empty">No matches</div>
          ) : (
            filtered.map((o) => {
              const checked = isSelected(o.value);

              return (
                <button
                  key={`${o.value}-${o.label}`}
                  type="button"
                  className={`ss-option ${checked ? "is-selected" : ""}`}
                  onMouseDown={(e) => e.preventDefault()}
                  onClick={() => (multiple ? toggleMulti(o.value) : pickSingle(o.value))}
                >
                  {multiple && (
                    <span className="ss-check" aria-hidden="true">
                      {checked ? "✓" : ""}
                    </span>
                  )}
                  <span className="ss-option-text">{o.label}</span>
                </button>
              );
            })
          )}
        </div>
      )}
    </div>
  );
}
