import React, { useEffect, useMemo, useState } from "react";
import api from "../../api/axios";
import { useLocation } from "react-router-dom";
import SearchableSelect from "../../components/SearchableSelect";
import "../../styles/AdminPages.css";
import "../../styles/ReadingsExplorer.css";

function useQuery() {
  const { search } = useLocation();
  return useMemo(() => new URLSearchParams(search), [search]);
}

export default function ReadingsExplorer() {
  const qs = useQuery();

  const [monitors, setMonitors] = useState([]);
  const [rows, setRows] = useState([]);
  const [err, setErr] = useState("");
  const [loadingMonitors, setLoadingMonitors] = useState(true);
  const [loadingRows, setLoadingRows] = useState(false);

  const [monitorId, setMonitorId] = useState(qs.get("monitorId") || "");
  const [type, setType] = useState("consumption"); // consumption | production | environment
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");

  const getMonitorId = (m) => (m.Monitor_ID ?? m.monitorId ?? m.id ?? "").toString();
  const monitorOptions = useMemo(
    () => monitors.map((m) => getMonitorId(m)).filter(Boolean),
    [monitors]
  );

  const loadMonitors = async () => {
    try {
      setErr("");
      setLoadingMonitors(true);
      const res = await api.get("/api/monitors");
      setMonitors(Array.isArray(res.data) ? res.data : []);
    } catch (e) {
      console.log("READINGS MONITORS ERROR:", e);
      setErr(e?.response?.data?.message || "Failed to load monitors.");
    } finally {
      setLoadingMonitors(false);
    }
  };

  const fetchReadings = async () => {
    if (!monitorId) {
      setErr("Please select a monitor.");
      return;
    }

    try {
      setErr("");
      setLoadingRows(true);

      const params = new URLSearchParams();
      params.set("monitorId", monitorId);
      params.set("type", type);
      if (from) params.set("from", from);
      if (to) params.set("to", to);

      const res = await api.get(`/api/readings?${params.toString()}`);
      setRows(Array.isArray(res.data) ? res.data : []);
    } catch (e) {
      console.log("READINGS FETCH ERROR:", e);
      setErr(
        e?.response?.data?.message ||
          "Failed to load readings. (Check your backend route /api/readings)"
      );
      setRows([]);
    } finally {
      setLoadingRows(false);
    }
  };

  useEffect(() => {
    loadMonitors();
  }, []);

  return (
    <section className="admin-page readings-page">
      <div className="admin-page-header">
        <h2 className="section-title">Readings Explorer</h2>

        <div className="readings-actions">
          <button
            type="button"
            className="cta-button"
            onClick={fetchReadings}
            disabled={loadingRows || loadingMonitors}
          >
            Load
          </button>
        </div>
      </div>

      <div className="readings-filters">
        <SearchableSelect
          label="Monitor ID"
          value={monitorId}
          onChange={setMonitorId}
          options={monitorOptions}
          placeholder={loadingMonitors ? "Loading..." : "Select monitor..."}
          allowAll={false}
        />

        <div className="form-group readings-type">
          <label htmlFor="reading-type">Type</label>
          <select
            id="reading-type"
            value={type}
            onChange={(e) => setType(e.target.value)}
          >
            <option value="consumption">consumption</option>
            <option value="production">production</option>
            <option value="environment">environment</option>
          </select>
        </div>

        <div className="form-group readings-date">
          <label htmlFor="from-date">From</label>
          <input id="from-date" type="date" value={from} onChange={(e) => setFrom(e.target.value)} />
        </div>

        <div className="form-group readings-date">
          <label htmlFor="to-date">To</label>
          <input id="to-date" type="date" value={to} onChange={(e) => setTo(e.target.value)} />
        </div>
      </div>

      {err && <div className="admin-error">{err}</div>}

      <div className="admin-table-wrap">
        <table className="admin-table-table">
          <thead>
            <tr>
              <th>Timestamp</th>
              <th>Field 1</th>
              <th>Field 2</th>
              <th>Field 3</th>
              <th>Field 4</th>
            </tr>
          </thead>

          <tbody>
            {loadingRows ? (
              <tr>
                <td colSpan={5} className="readings-empty">Loading readings...</td>
              </tr>
            ) : rows.length === 0 ? (
              <tr>
                <td colSpan={5} className="readings-empty">No readings loaded.</td>
              </tr>
            ) : (
              rows.map((r, idx) => (
                <tr key={r.id ?? idx}>
                  <td>{r.timestamp ?? r.Timestamp ?? r.time ?? "—"}</td>
                  <td>{r.field1 ?? "—"}</td>
                  <td>{r.field2 ?? "—"}</td>
                  <td>{r.field3 ?? "—"}</td>
                  <td>{r.field4 ?? "—"}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      <div className="readings-note">
        If your backend returns different column names, map them in the table body.
        The UI is ready; only the endpoint response shape needs matching.
      </div>
    </section>
  );
}
