import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../../styles/AdminPages.css";

export default function UsersList() {
  const [users, setUsers] = useState([]);
  const [err, setErr] = useState("");

  async function deleteUser(email) {
    try {
      await axios.delete(`https://localhost:5000/api/users/${email}`);
      window.location.reload();
    } catch (err) {
      console.error("Error deleting user:", err);
    }
  }

  useEffect(() => {
    axios
      .get("/api/users")
      .then((res) => setUsers(res.data))
      .catch(() => setErr("Failed to load users."));
  }, []);

  return (
    <section className="admin-page">
      <div className="admin-page-header">
        <h2 className="section-title">Users</h2>
        <Link className="cta-button" to="/admin/users/new">
          Create User
        </Link>
      </div>

      {err && <p className="form-error">{err}</p>}

      <div className="admin-table">
        <table>
          <thead className="admin-table-row admin-table-head">
            <th>Name</th>
            <th>Email</th>
            <th>Phone Number</th>
            <th>Monitor Type</th>
            <th>Subscription Type</th>
          </thead>

          <tbody>
            {users.map((u, idx) => (
              <tr key={idx} className="admin-table-row">
                <td>{u.name}</td>
                <td>{u.email}</td>
                <td>{u.phoneNumber}</td>
                <td>{u.monitorType}</td>
                <td>{u.subscriptionType}</td>
                <td>
                <button onClick={deleteUser(u.email)}></button>
                </td>
              </tr>
            ))}
          </tbody>

        </table>
      </div>
    </section>
  );
}
