import React, { useEffect, useMemo, useState } from "react";
import api from "../../api/axios";
import { useLocation } from "react-router-dom";
import Chart from "chart.js/auto";
import SearchableSelect from "../../components/SearchableSelect";
import DataTable from "../../components/DataTable";
import ReadingsChart from "../../components/ReadingsChart";
import ChartCard from "../../components/charts/ChartCard";
import LineTimeChart from "../../components/charts/LineTimeChart";
import "../../styles/AdminPages.css";
import "../../styles/ReadingsExplorer.css";

function useQuery() {
  const { search } = useLocation();
  return useMemo(() => new URLSearchParams(search), [search]);
}

export default function ReadingsExplorer() {
  const qs = useQuery();

  const [monitors, setMonitors] = useState([]);
  const [err, setErr] = useState("");
  const [loadingMonitors, setLoadingMonitors] = useState(true);
  const [loadingRows, setLoadingRows] = useState(false);
  const [viewMode, setViewMode] = useState("table"); // "table" | "chart"


  const [data, setData] = useState(null);


  const [monitorId, setMonitorId] = useState(qs.get("monitorId") || "");
  const [tableOption, setTableOption] = useState("consumption"); // consumption | production | environment
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");

  const getMonitorId = (m) => (m.Monitor_ID ?? m.monitorId ?? m.id ?? "").toString();
  const monitorOptions = useMemo(
    () => monitors.map((m) => getMonitorId(m)).filter(Boolean),
    [monitors]
  );
  const tableOptions = useMemo(
    () => ["consumption", "production", "environment"],
    []
  );

  const loadMonitors = async () => {
    try {
      setErr("");
      setLoadingMonitors(true);
      const res = await api.get("/api/monitors");
      setMonitors(Array.isArray(res.data) ? res.data : []);
    } catch (e) {
      console.log("READINGS MONITORS ERROR:", e);
      setErr(e?.response?.data?.message || "Failed to load monitors.");
    } finally {
      setLoadingMonitors(false);
    }
  };

const fetchMonitorData = async () => {
  if (!monitorId) {
    setErr("Please select a monitor.");
    return;
  }

  try {
    setErr("");
    setLoadingRows(true);

    const params = new URLSearchParams();
    if (from) params.set("from", from);
    if (to) params.set("to", to);
    params.set("limit", "200");

    const res = await api.get(`/api/monitor/${monitorId}/data?${params.toString()}`);
    setData(res.data);
  } catch (e) {
    console.log("FETCH MONITOR DATA ERROR:", e);
    setErr(e?.response?.data?.message || "Failed to load monitor data.");
    setData(null);
  } finally {
    setLoadingRows(false);
  }
};

  useEffect(() => {loadMonitors();}, []);

  useEffect(() => {
    if (!data) return;

    const destroyIfExists = (id) => {
      const chart = Chart.getChart(id);
      if (chart) chart.destroy();
    };

    destroyIfExists("consumptionChart");
    destroyIfExists("productionChart");

    new Chart(document.getElementById("consumptionChart"), {
      type: "line",
      data: {
        labels: data.energy_consumption.map(r => r.Timestamp),
        datasets: [{
          label: "Consumption",
          data: data.energy_consumption.map(r => r.Consumption_Value),
          borderColor: "#22d3ee",
          tension: 0.3,
        }],
      },
    });

    new Chart(document.getElementById("productionChart"), {
      type: "line",
      data: {
        labels: data.energy_production.map(r => r.Timestamp),
        datasets: [{
          label: "Production",
          data: data.energy_production.map(r => r.Production_Value),
          borderColor: "#4ade80",
          tension: 0.3,
        }],
      },
    });
  }, [data]);

  return (
    <section className="admin-page readings-page">
      <div className="admin-page-header">
        <h2 className="section-title">Readings Explorer</h2>

        <div className="readings-actions">
          <button
            type="button"
            className="cta-button"
            onClick={fetchMonitorData}
            disabled={loadingRows || loadingMonitors}
          >
            Load
          </button>
        </div>
      </div>

      <div className="form-group readings-filters">
        <div className="form-group readings-type">
          <SearchableSelect
            label="Monitor ID"
            value={monitorId}
            onChange={setMonitorId}
            options={monitorOptions}
            placeholder={loadingMonitors ? "Loading..." : "Select monitor..."}
            allowAll={false}
          />
          <SearchableSelect
            label="Reading Type"
            value={tableOption}
            onChange={setTableOption}
            options={tableOptions}
            placeholder="Select reading type..."
            allowAll={false}
            searchable={false}
          />
        </div>

        <div className="form-group readings-date">
          <label htmlFor="from-date">From</label>
          <input id="from-date" type="date" value={from} onChange={(e) => setFrom(e.target.value)} />
        </div>

        <div className="form-group readings-date">
          <label htmlFor="to-date">To</label>
          <input id="to-date" type="date" value={to} onChange={(e) => setTo(e.target.value)} />
        </div>
      </div>

      {err && <div className="admin-error">{err}</div>}

      {data && (
        <div className="readings-split">
          <div className="readings-left">
          <DataTable title="Monitors" rows={data.monitors} />
          <DataTable title="Energy Consumption" rows={data.energy_consumption} />
          <DataTable title="Energy Production" rows={data.energy_production} />
          <DataTable title="Environmental Data" rows={data.environmental_data} />
          <DataTable title="Energy Reserves" rows={data.energy_reserves} />
          <DataTable title="Notifications" rows={data.notifications} />
          <DataTable title="Sell Requests" rows={data.sell_request} />
          <DataTable title="Solar System Data" rows={data.solar_system_data} />
          </div>
          <div className="readings-right">
            <h3 className="charts-title">Overview</h3>

            <div className="chart-card">
              <canvas id="consumptionChart" />
            </div>

            <div className="chart-card">
              <canvas id="productionChart" />
            </div>
          </div>
        </div>
      )}

      <div className="readings-note">
        If your backend returns different column names, map them in the table body.
        The UI is ready; only the endpoint response shape needs matching.
      </div>
    </section>
  );
}
