import React, { useEffect, useMemo, useState } from "react";
import api from "../../api/axios";
import SearchableSelect from "../../components/SearchableSelect";
import "../../styles/AdminPages.css";
import "../../styles/HousesList.css";

export default function HousesList() {
  const [monitors, setMonitors] = useState([]);
  const [err, setErr] = useState("");
  const [loading, setLoading] = useState(true);

  // filters
  const [fOwner, setFOwner] = useState("");
  const [fLocation, setFLocation] = useState("");
  const [fStatus, setFStatus] = useState("");

  const normalize = (v) => (v ?? "").toString().toLowerCase();
  const getMonitorId = (m) => (m.Monitor_ID ?? "").toString();
  const getOwner = (m) => (m.User_Email ?? "").toString();
  const getLocation = (m) => (m.Location ?? "").toString();
  const getStatus = (m) => (m.Status ?? "").toString();

  const load = async () => {
    try {
      setErr("");
      setLoading(true);
      const res = await api.get("/api/monitors");
      setMonitors(Array.isArray(res.data) ? res.data : []);
    } catch (e) {
      console.log("HOUSES LOAD ERROR:", e);
      setErr(e?.response?.data?.message || "Failed to load houses (from monitors).");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);


  const ownerOptions = useMemo(() => monitors.map((m) => getOwner(m)).filter(Boolean), [monitors]);
  const locationOptions = useMemo(() => monitors.map((m) => getLocation(m)).filter(Boolean), [monitors]);
  const statusOptions = useMemo(() => ["online", "offline", "maintenance"], []);

  const filtered = useMemo(() => {
    const contains = (cell, typed) => normalize(cell).includes(normalize(typed).trim());

    return houses.filter((h) => {
      if (fOwner && !contains(h.owner, fOwner)) return false;
      if (fLocation && !contains(h.location, fLocation)) return false;

      // status filter: keep houses that have >= 1 monitor with that status
      if (fStatus) {
        const s = normalize(fStatus);
        const c = h.statusCounts[s] ?? 0;
        if (c <= 0) return false;
      }

      return true;
    });
  }, [houses, fOwner, fLocation, fStatus]);

  const clearFilters = () => {
    setFOwner("");
    setFLocation("");
    setFStatus("");
  };

  return (
    <section className="admin-page houses-page">
      <div className="admin-page-header">
        <h2 className="section-title">Houses</h2>

        <div className="houses-actions">
          <button type="button" className="secondary-button" onClick={load}>
            Refresh
          </button>
        </div>
      </div>

      <div className="houses-filters">
        <SearchableSelect label="Owner Email" value={fOwner} onChange={setFOwner} options={ownerOptions} />
        <SearchableSelect label="Location" value={fLocation} onChange={setFLocation} options={locationOptions} />
        <SearchableSelect label="Has Status" value={fStatus} onChange={setFStatus} options={statusOptions} />
        <button type="button" className="secondary-button houses-clear" onClick={clearFilters}>
          Clear filters
        </button>
      </div>

      {err && <div className="admin-error">{err}</div>}

      <div className="admin-table">
        <table className="admin-table-table">
          <thead>
            <tr>
              <th>Owner Email</th>
              <th>Location</th>
              <th>Status</th>
              <th>Monitor ID</th>
            </tr>
          </thead>

          <tbody>
            {loading ? (
              <tr>
                <td colSpan={4} className="houses-empty">Loading houses...</td>
              </tr>
            ) : filtered.length === 0 ? (
              <tr>
                <td colSpan={4} className="houses-empty">No houses found.</td>
              </tr>
            ) : (
              filtered.map((h) => {
                // --- ONE status string ---
                // const statuses = h.monitors.map((x) => normalize(x.status));
                // let houseStatus = "unknown";
                // if (statuses.includes("online")) houseStatus = "online";
                // else if (statuses.includes("maintenance")) houseStatus = "maintenance";
                // else if (statuses.includes("offline")) houseStatus = "offline";
                // else if (statuses.some(Boolean)) houseStatus = statuses.find(Boolean);

                // // --- ONE monitor id ---
                // const ids = h.monitors.map((x) => x.monitorId).filter(Boolean);

                // // pick lowest numeric id if possible, else first
                // let primaryMonitorId = ids[0] || "";
                // const numericIds = ids
                //   .map((id) => Number(id))
                //   .filter((n) => !Number.isNaN(n));

                // if (numericIds.length > 0) {
                //   primaryMonitorId = String(Math.min(...numericIds));
                // }

                return (
                  <tr key={`${h.owner}-${h.location}`}>
                    <td>{h.owner || "—"}</td>
                    <td>{h.location || "—"}</td>
                    <td>{houseStatus || "—"}</td>
                    <td>{h.monitorId || "—"}</td>
                  </tr>
                );
              })
            )}
          </tbody>
        </table>
      </div>
    </section>
  );
}
