import React, { useEffect, useState } from "react";
import api from "../../api/axios";
import { Link } from "react-router-dom";
import "../../styles/AdminPages.css";

export default function UsersList() {
  const [users, setUsers] = useState([]);
  const [err, setErr] = useState("");
  const [editOpen, setEditOpen] = useState(false);
  const [editOriginalEmail, setEditOriginalEmail] = useState("");
  const [editEmail, setEditEmail] = useState("");       // display only
  const [editIsAdmin, setEditIsAdmin] = useState(false);
  const [editPassword, setEditPassword] = useState(""); // optional
  const [editSaving, setEditSaving] = useState(false);

  const [confirmEmail, setConfirmEmail] = useState(null);
  
    useEffect(() => {
      api
        .get("/api/users")
        .then((res) => setUsers(res.data))
        .catch(() => setErr("Failed to load users."));
    }, []);

    useEffect(() => {
      if (!confirmEmail) return;

      const onKeyDown = (e) => {
        if (e.key === "Escape") setConfirmEmail(null);
      };

      window.addEventListener("keydown", onKeyDown);
      return () => window.removeEventListener("keydown", onKeyDown);
    }, [confirmEmail]);

    const openEdit = (u) => {
      setErr("");
      setEditOriginalEmail(u.email);
      setEditEmail(u.email);              // display only
      setEditIsAdmin(!!u.isAdmin);
      setEditPassword("");                // optional
      setEditOpen(true);
    };
    
  const deleteUser = async (email) => {
    try {
      await api.delete(`/api/users/${email}`);
      setConfirmEmail(null);
      window.location.reload();
    } catch (err) {
      console.error("Error deleting user:", err);
    }
  }

  return (
    <section className="admin-page">
      <div className="admin-page-header">
        <h2 className="section-title">Users</h2>
        <Link className="cta-button" to="/admin/users/new">
          Create User
        </Link>
      </div>

      {err && <p className="form-error">{err}</p>}

      <div className="admin-table">
        <div className="admin-table-scroll">
          <table className="admin-table-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone Number</th>
                <th>Monitor Type</th>
                <th>Subscription Type</th>
                <th className="th-actions">Actions</th>
              </tr>
            </thead>

            <tbody>
              {users.map((u, idx) => (
                <tr key={idx}>
                  <td>{u.name}</td>
                  <td>{u.email}</td>
                  <td>{u.phoneNumber}</td>
                  <td>{u.monitorType}</td>
                  <td>{u.subscriptionType}</td>
                  <td className="td-actions">
                    <button className="danger-btn" onClick={() => setConfirmEmail(u.email)}>Delete</button>
                  </td>
                </tr>
              ))}
              {confirmEmail && (
                <div
                  className="modal-backdrop modal-fade"
                  role="dialog"
                  aria-modal="true"
                  onMouseDown={() => setConfirmEmail(null)} // click backdrop closes
                >
                  <div
                    className="modal modal-pop"
                    onMouseDown={(e) => e.stopPropagation()} // prevent closing when clicking inside
                  >
                    <h3 className="modal-title">Delete user?</h3>
                    <p className="modal-text">
                      Are you sure you want to delete <b>{confirmEmail}</b>?
                      <br />
                      This action cannot be undone.
                    </p>

                    <div className="modal-actions">
                      <button
                        type="button"
                        className="danger-button"
                        onClick={() => deleteUser(confirmEmail)}
                      >
                        Yes, delete
                      </button>

                      <button
                        type="button"
                        className="secondary-button"
                        onClick={() => setConfirmEmail(null)}
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                </div>
              )}


            </tbody>
          </table>
        </div>
      </div>
    </section>
  );
}
