// src/components/analytics/Analytics.jsx
import React, { useMemo } from "react";
import ChartCard from "../charts/ChartCard";
import LineTimeChart from "../charts/LineTimeChart";
import BarTimeChart from "../charts/BarTimeChart";
import MetricsGrid from "./MetricsGrid";
import { useAnalyticsData } from "./hooks/useAnalyticsData";
import EnergyMixPie from "./EnergyMixPie"; // we’ll add this below

function safeParse(json, fallback) {
  try { return JSON.parse(json) ?? fallback; } catch { return fallback; }
}

const asMs = (v) => {
  const t = new Date(v).getTime();
  return Number.isFinite(t) ? t : null;
};

const normalizeDaily = (rows, dateKey) =>
  (rows || [])
    .map((r) => ({ ...r, Timestamp: asMs(r?.[dateKey] ?? r?.Timestamp ?? r?.Date) }))
    .filter((r) => r.Timestamp != null)
    .sort((a, b) => a.Timestamp - b.Timestamp);

const normalizeMonthly = (rows, valueKey) =>
  (rows || [])
    .map((r) => {
      const y = Number(r?.Year);
      const m = Number(r?.Month);
      if (!y || !m) return null;
      return { ...r, Timestamp: asMs(`${y}-${String(m).padStart(2, "0")}-01`) };
    })
    .filter(Boolean)
    .sort((a, b) => a.Timestamp - b.Timestamp);

export default function Analytics() {
  const monitors = useMemo(() => safeParse(localStorage.getItem("monitors"), []), []);
  const monitorId = useMemo(() => {
    const first = monitors?.[0];
    const id = first?.Monitor_ID ?? first?.monitor_id;
    return id != null ? String(id) : "";
  }, [monitors]);

  const {
    loading,
    err,
    todayCons,
    todayProd,
    solarTodayEff,
    avgTemp7,
    avgHumidity7,
    consD,
    prodD,
    resD,
    solD,
    consM,
    prodM,
  } = useAnalyticsData(monitorId);

  // Normalize datasets for your LineTimeChart
  const consDaily = useMemo(() => normalizeDaily(consD, "Timestamp"), [consD]);
  const prodDaily = useMemo(() => normalizeDaily(prodD, "Timestamp"), [prodD]);
  const reservesDaily = useMemo(() => normalizeDaily(resD, "Timestamp"), [resD]);

  // Solar uses Date_Calculated in your project a lot
  const solarDaily = useMemo(() => normalizeDaily(solD, "Date_Calculated"), [solD]);

  // Monthly tables: build a Timestamp for each month
  const consMonthly = useMemo(() => normalizeMonthly(consM, "Total_Consumption"), [consM]);
  const prodMonthly = useMemo(() => normalizeMonthly(prodM, "Total_Production"), [prodM]);

  return (
    <section className="analytics-section" id="analytics">
      <div className="dashboard-container">
        <h2 className="section-title">Analytics</h2>

        {!monitorId && <div className="muted">No monitor found for this account.</div>}
        {loading && monitorId && <div className="muted">Loading analytics...</div>}
        {err && <div className="error">{err}</div>}

        <MetricsGrid
          metrics={[
            { value: `${todayCons.toFixed(2)} kWh`, label: "Today's Consumption" },
            { value: `${todayProd.toFixed(2)} kWh`, label: "Today's Production" },
            { value: `${solarTodayEff.toFixed(1)}%`, label: "Panel Efficiency (Today)" },
            { value: `${avgTemp7.toFixed(1)}°C`, label: "Avg Temp (7d)" },
            { value: `${avgHumidity7.toFixed(1)}%`, label: "Avg Humidity (7d)" },
          ]}
        />

        <div className="charts-grid">
          <ChartCard title="Daily Consumption (kWh)">
            <LineTimeChart
              rows={consDaily}
              xKey="Timestamp"
              yKeys={["Total_Consumption"]}
            />
          </ChartCard>

          <ChartCard title="Daily Production (kWh)">
            <LineTimeChart
              rows={prodDaily}
              xKey="Timestamp"
              yKeys={["Total_Production"]}
            />
          </ChartCard>

          <ChartCard title="Solar Panel Efficiency (Daily)">
  <BarTimeChart
    rows={solarDailyRows}          // your solar daily array
    xKey="Date_Calculated"         // IMPORTANT for solar
    yKeys={["Panel_Efficiency"]}
    labels={{ Panel_Efficiency: "Efficiency (%)" }}
  />
</ChartCard>

<ChartCard title="Energy Reserves (Daily)">
  <BarTimeChart
    rows={reservesDailyRows}
    xKey="Timestamp"               // or Date depending on your row shape
    yKeys={["Reserve_Amount"]}
    labels={{ Reserve_Amount: "Reserve Amount" }}
  />
</ChartCard>


          <ChartCard title="Reserves Trend">
            <LineTimeChart
              rows={reservesDaily}
              xKey="Timestamp"
              yKeys={["Reserve_Amount"]}
            />
          </ChartCard>

          <ChartCard title="Monthly Consumption vs Production">
            <LineTimeChart
              rows={consMonthly.map((r, i) => ({
                ...r,
                Total_Production: prodMonthly[i]?.Total_Production ?? 0,
              }))}
              xKey="Timestamp"
              yKeys={["Total_Consumption", "Total_Production"]}
            />
          </ChartCard>

          <ChartCard title="Energy Mix (Last 7 days)">
            <EnergyMixPie
              consumptionRows={consDaily}
              productionRows={prodDaily}
              solarRows={solarDaily}
            />
          </ChartCard>
        </div>
      </div>
    </section>
  );
}
