import React, { useEffect, useMemo, useRef, useState } from "react";
import "../styles/SearchableSelect.css";

export default function SearchableSelect({
  label,
  value,
  onChange,
  options,
  placeholder = "All",
  allowAll = true,
}) {
  const [open, setOpen] = useState(false);
  const [text, setText] = useState(value ?? "");
  const wrapRef = useRef(null);

  // Keep input text in sync if parent changes it (e.g., Clear All)
  useEffect(() => {
    setText(value ?? "");
  }, [value]);

  // Close on outside click
  useEffect(() => {
    const onDocMouseDown = (e) => {
      if (!wrapRef.current) return;
      if (!wrapRef.current.contains(e.target)) setOpen(false);
    };
    document.addEventListener("mousedown", onDocMouseDown);
    return () => document.removeEventListener("mousedown", onDocMouseDown);
  }, []);

  const filteredOptions = useMemo(() => {
    const q = (text ?? "").toString().trim().toLowerCase();
    const clean = (options || [])
      .map((x) => (x ?? "").toString())
      .filter(Boolean);

    const uniq = Array.from(new Set(clean));

    if (!q) return uniq;
    return uniq.filter((opt) => opt.toLowerCase().includes(q));
  }, [options, text]);

  const handleInput = (e) => {
    const v = e.target.value;
    setText(v);
    setOpen(true);
    // live filter table as you type
    onChange(v);
  };

  const selectOption = (opt) => {
    setText(opt);
    onChange(opt);
    setOpen(false);
  };

  const clear = () => {
    setText("");
    onChange("");
    setOpen(false);
  };

  return (
    
    <div className="ss-field" ref={wrapRef}>
      {label ? <label className="ss-label">{label}</label> : null}

      <div className={`ss-control ${open ? "is-open" : ""}`}>
        <input
          className="ss-input"
          value={text}
          onChange={handleInput}
          onFocus={() => setOpen(true)}
          placeholder={placeholder}
        />

        {!!text && (
          <button type="button" className="ss-clear" onClick={clear} aria-label="Clear">
            ×
          </button>
        )}

        <button
          type="button"
          className="ss-caret"
          onClick={() => setOpen((p) => !p)}
          aria-label="Toggle options"
        >
          ▾
        </button>
      </div>

      {open && (
        <div className="ss-menu" role="listbox">
          {allowAll && (
            <button type="button" className="ss-option" onClick={() => selectOption("")}>
              All
            </button>
          )}

          {filteredOptions.length === 0 ? (
            <div className="ss-empty">No matches</div>
          ) : (
            filteredOptions.map((opt) => (
              <button
                type="button"
                className="ss-option"
                key={opt}
                onClick={() => selectOption(opt)}
              >
                {opt}
              </button>
            ))
          )}
        </div>
      )}
    </div>
  );
}
