import React from "react";
import StatCard from "./StatCard";
import { useDashboardStats } from "../ana/hooks/useDashboardStats";

export default function Dashboard({ monitorId }) {
  const { stats, err, loading, data } = useDashboardStats(monitorId);

  return (
    <section className="dashboard-section" id="dashboard">
      <div className="dashboard-container">
        <h2 className="section-title">Dashboard Overview</h2>

        {/* THESE 3 LINES WILL TELL YOU EXACTLY WHY IT'S EMPTY */}
        {!monitorId && (
          <div className="error">No monitorId was provided to Dashboard.</div>
        )}
        {loading && <div className="muted">Loading dashboard data...</div>}
        {err && <div className="error">{err}</div>}

        {/* Extra debug (remove later) */}
        <div className="muted" style={{ fontSize: 12, opacity: 0.8 }}>
          monitorId: {String(monitorId)} | stats: {stats.length} | hasData:{" "}
          {data ? "yes" : "no"}
        </div>

        <div className="stats-grid">
          {stats.map((stat) => (
            <StatCard key={stat.title} {...stat} />
          ))}
        </div>
      </div>
    </section>
  );
}
