// src/components/dashboard/Dashboard.jsx
import React, { useMemo } from "react";
import StatCard from "./StatCard";

// Example: you can later replace these with API data.
// For now this makes your dashboard look legit even with dummy arrays.
export default function Dashboard() {
  // Fake trend data (7 points) ‚Äî replace with real computed arrays later
  const stats = useMemo(
    () => [
      {
        icon: "üîå",
        title: "Today's Consumption",
        value: "12.4 kWh",
        description: "Up 8% vs yesterday. Peak usage was around 7‚Äì9 PM.",
        color: "#00ccff",
        series: [8, 9, 10, 11, 10, 12, 12.4],
      },
      {
        icon: "‚òÄÔ∏è",
        title: "Today's Solar Production",
        value: "9.1 kWh",
        description: "Strong midday output. Panel efficiency stable.",
        color: "#ffcc00",
        series: [1, 2, 4, 7, 9, 8, 9.1],
      },
      {
        icon: "‚öñÔ∏è",
        title: "Net Energy",
        value: "-3.3 kWh",
        description: "You consumed more than produced today.",
        color: "#ff6b6b",
        series: [-2, -1.5, -0.5, 0.2, -1, -2.4, -3.3],
      },
      {
        icon: "‚ö°",
        title: "Current Power",
        value: "640 W",
        description: "Live estimate from latest reading.",
        color: "#4ecdc4",
        series: [520, 600, 580, 720, 680, 650, 640],
      },
      {
        icon: "üìà",
        title: "This Month (Consumption)",
        value: "312 kWh",
        description: "Projected to finish ~360 kWh at current pace.",
        color: "#00ffcc",
        series: [220, 235, 250, 268, 280, 300, 312],
      },
      {
        icon: "üí∏",
        title: "Estimated Cost (Month)",
        value: "AED 142",
        description: "Based on your tariff estimate. Update anytime.",
        color: "#ff0080",
        series: [60, 72, 84, 101, 116, 133, 142],
      },
    ],
    []
  );

  return (
    <section className="dashboard-section" id="dashboard">
      <div className="dashboard-container">
        <h2 className="section-title">Dashboard Overview</h2>
        <div className="stats-grid">
          {stats.map((stat) => (
            <StatCard key={stat.title} {...stat} />
          ))}
        </div>
      </div>
    </section>
  );
}
