import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function UsersList() {
  const [users, setUsers] = useState([]);
  const [q, setQ] = useState("");
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  async function loadUsers(search = "") {
    setLoading(true);
    setErr("");
    try {
      // If your backend doesn't support search yet, this still works (it just ignores ?search=)
      const res = await fetch(`/api/users${search ? `?search=${encodeURIComponent(search)}` : ""}`);
      const data = await res.json();
      if (!res.ok) throw new Error(data?.message || "Failed to load users");
      setUsers(Array.isArray(data) ? data : (data.users ?? []));
    } catch (e) {
      setErr(e.message || "Error");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    loadUsers();
  }, []);

  const onSearch = (e) => {
    const value = e.target.value;
    setQ(value);
    loadUsers(value);
  };

  return (
    <div style={{ maxWidth: 900 }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", gap: 12 }}>
        <h1 style={{ margin: 0 }}>Users</h1>
        <Link to="/admin/users/new" style={linkBtn}>
          + Create User
        </Link>
      </div>

      <div style={{ marginTop: 12 }}>
        <input
          value={q}
          onChange={onSearch}
          placeholder="Search by name or email…"
          style={inputStyle}
        />
      </div>

      {loading && <div style={{ marginTop: 14 }}>Loading…</div>}
      {err && <div style={{ marginTop: 14, color: "#fca5a5" }}>{err}</div>}

      {!loading && !err && (
        <div style={{ marginTop: 14, display: "grid", gap: 10 }}>
          {users.length === 0 ? (
            <div style={{ opacity: 0.8 }}>No users found.</div>
          ) : (
            users.map((u) => (
              <div key={u.id} style={cardStyle}>
                <div style={{ fontWeight: 700 }}>{u.name}</div>
                <div style={{ opacity: 0.85 }}>{u.email}</div>
                <div style={{ opacity: 0.7, fontSize: 13 }}>
                  {u.role} • {u.status}
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

const inputStyle = {
  width: "100%",
  maxWidth: 420,
  padding: "10px 12px",
  borderRadius: 12,
  border: "1px solid rgba(148,163,184,0.35)",
  background: "transparent",
  color: "inherit",
};

const cardStyle = {
  padding: 12,
  borderRadius: 14,
  border: "1px solid rgba(148,163,184,0.35)",
};

const linkBtn = {
  padding: "10px 14px",
  borderRadius: 999,
  border: "1px solid rgba(148,163,184,0.35)",
  textDecoration: "none",
  color: "inherit",
};
