import React, { createContext, useContext, useEffect, useState } from "react";

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const [account, setUser] = useState(() => {
    const savedUser = localStorage.getItem("account");
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [token, setToken] = useState(() => {
    const savedToken = localStorage.getItem("token");
    return savedToken ? savedToken : null;
  });

  useEffect(() => {
    if (account) localStorage.setItem("account", JSON.stringify(account));
    else localStorage.removeItem("account");
  }, [account]);

  useEffect(() => {
    if (token) localStorage.setItem("token", token);
    else localStorage.removeItem("token");
  }, [token]);
  
  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem("account");
    localStorage.removeItem("token");
  };
  
  return (
    <AuthContext.Provider value={{ 
      account: account,
      setUser,
      token,
      setToken,
      logout,
      isAuthenticated: !!account && !!token
      }}>
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
