import React, { useEffect, useMemo, useState } from "react";
import api from "../../api/axios";
import { useNavigate } from "react-router-dom";
import "../../styles/AdminPages.css";

export default function MonitorsList() {
  const navigate = useNavigate();

  const [monitors, setMonitors] = useState([]);
  const [err, setErr] = useState("");
  const [loading, setLoading] = useState(true);

  // search + filters
  const [q, setQ] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");

  // delete modal
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  // edit modal
  const [editOpen, setEditOpen] = useState(false);
  const [editId, setEditId] = useState("");
  const [editUserEmail, setEditUserEmail] = useState("");
  const [editLocation, setEditLocation] = useState("");
  const [editStatus, setEditStatus] = useState("offline");
  const [editMicro, setEditMicro] = useState("");
  const [editInstallDate, setEditInstallDate] = useState(""); // "YYYY-MM-DD"
  const [saving, setSaving] = useState(false);

  const loadMonitors = async () => {
    try {
      setErr("");
      setLoading(true);
      const res = await api.get("/api/monitors");
      setMonitors(Array.isArray(res.data) ? res.data : []);
    } catch (e) {
      console.log("LOAD MONITORS ERROR:", e);
      setErr(e?.response?.data?.message || "Failed to load monitors.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadMonitors();
  }, []);

  // helpers
  const normalize = (v) => (v ?? "").toString().toLowerCase();

  const filtered = useMemo(() => {
    const query = normalize(q).trim();

    return monitors.filter((m) => {
      const id = normalize(m.Monitor_ID ?? m.monitorId ?? m.id);
      const email = normalize(m.User_Email ?? m.userEmail ?? m.email);
      const loc = normalize(m.Location ?? m.location);

      const status = normalize(m.Status ?? m.status);

      const matchesQuery =
        !query ||
        id.includes(query) ||
        email.includes(query) ||
        loc.includes(query);

      const matchesStatus =
        statusFilter === "all" || status === normalize(statusFilter);

      return matchesQuery && matchesStatus;
    });
  }, [monitors, q, statusFilter]);

  const openEdit = (m) => {
    setErr("");
    const id = (m.Monitor_ID ?? m.monitorId ?? m.id ?? "").toString();
    const email = (m.User_Email ?? m.userEmail ?? m.email ?? "").toString();
    const loc = (m.Location ?? m.location ?? "").toString();
    const status = (m.Status ?? m.status ?? "offline").toString();
    const micro = (m.Microprocessor_Type ?? m.microprocessorType ?? "").toString();
    const rawDate = m.Installation_Date ?? m.installationDate ?? "";

    // If backend returns full ISO date-time, keep only YYYY-MM-DD
    const dateOnly =
      typeof rawDate === "string" && rawDate.includes("T")
        ? rawDate.split("T")[0]
        : (rawDate || "").toString().slice(0, 10);

    setEditId(id);
    setEditUserEmail(email);
    setEditLocation(loc);
    setEditStatus(status);
    setEditMicro(micro);
    setEditInstallDate(dateOnly);

    setEditOpen(true);
  };

  const closeEdit = () => {
    setEditOpen(false);
    setSaving(false);
  };

  const saveEdit = async (e) => {
    e.preventDefault();
    if (!editId) return;

    try {
      setSaving(true);
      setErr("");

      const payload = {
        User_Email: editUserEmail.trim(),
        Location: editLocation.trim(),
        Status: editStatus,
        Microprocessor_Type: editMicro.trim(),
        Installation_Date: editInstallDate || null,
      };

      // endpoint: PUT /api/monitors/:id
      const res = await api.put(`/api/monitors/${encodeURIComponent(editId)}`, payload);

      // If backend returns updated monitor, use it; else update locally with payload
      const updated = res.data?.monitor
        ? res.data.monitor
        : { Monitor_ID: editId, ...payload };

      setMonitors((prev) =>
        prev.map((m) => {
          const id = (m.Monitor_ID ?? m.monitorId ?? m.id ?? "").toString();
          return id === editId ? { ...m, ...updated } : m;
        })
      );

      closeEdit();
    } catch (e2) {
      console.log("SAVE MONITOR ERROR:", e2);
      setErr(e2?.response?.data?.message || "Failed to update monitor.");
      setSaving(false);
    }
  };

  const deleteMonitor = async (id) => {
    try {
      setErr("");
      await api.delete(`/api/monitors/${encodeURIComponent(id)}`);
      setMonitors((prev) =>
        prev.filter((m) => ((m.Monitor_ID ?? m.monitorId ?? m.id ?? "") + "") !== (id + ""))
      );
      setConfirmDeleteId(null);
    } catch (e) {
      console.log("DELETE MONITOR ERROR:", e);
      setErr(e?.response?.data?.message || "Failed to delete monitor.");
    }
  };

  const goToReadings = (id) => {
    navigate(`/admin/readings?monitorId=${encodeURIComponent(id)}`);
  };

  return (
    <section className="admin-page">
      <div className="admin-page-header">
        <h2 className="section-title">Monitors</h2>

        <div style={{ display: "flex", gap: 10, alignItems: "center" }}>
          <button type="button" className="secondary-button" onClick={loadMonitors}>
            Refresh
          </button>
          {/* If you later create a monitor creation page */}
          {/* <button type="button" className="cta-button" onClick={() => navigate("/admin/monitors/new")}>
            Add Monitor
          </button> */}
        </div>
      </div>

      {/* controls */}
      <div className="admin-controls" style={{ display: "flex", gap: 12, flexWrap: "wrap" }}>
        <div className="form-group" style={{ minWidth: 260 }}>
          <label htmlFor="monitor-search">Search</label>
          <input
            id="monitor-search"
            type="text"
            value={q}
            onChange={(e) => setQ(e.target.value)}
            placeholder="Monitor ID, user email, location..."
          />
        </div>

        <div className="form-group" style={{ minWidth: 200 }}>
          <label htmlFor="status-filter">Status</label>
          <select
            id="status-filter"
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
          >
            <option value="all">All</option>
            <option value="online">online</option>
            <option value="offline">offline</option>
            <option value="maintenance">maintenance</option>
          </select>
        </div>
      </div>

      {err && <div className="admin-error">{err}</div>}

      <div className="admin-table-wrap">
        <table className="admin-table">
          <thead>
            <tr>
              <th>Monitor ID</th>
              <th>User Email</th>
              <th>Location</th>
              <th>Status</th>
              <th>Microprocessor</th>
              <th>Installed</th>
              <th style={{ width: 280 }}>Actions</th>
            </tr>
          </thead>

          <tbody>
            {loading ? (
              <tr>
                <td colSpan={7} style={{ textAlign: "center", padding: 18 }}>
                  Loading monitors...
                </td>
              </tr>
            ) : filtered.length === 0 ? (
              <tr>
                <td colSpan={7} style={{ textAlign: "center", padding: 18 }}>
                  No monitors found.
                </td>
              </tr>
            ) : (
              filtered.map((m) => {
                const id = (m.Monitor_ID ?? m.monitorId ?? m.id ?? "").toString();
                const email = (m.User_Email ?? m.userEmail ?? m.email ?? "").toString();
                const loc = (m.Location ?? m.location ?? "").toString();
                const status = (m.Status ?? m.status ?? "").toString();
                const micro = (m.Microprocessor_Type ?? m.microprocessorType ?? "").toString();
                const installedRaw = m.Installation_Date ?? m.installationDate ?? "";
                const installed =
                  typeof installedRaw === "string" && installedRaw.includes("T")
                    ? installedRaw.split("T")[0]
                    : (installedRaw || "").toString().slice(0, 10);

                return (
                  <tr key={id || `${email}-${loc}`}>
                    <td>{id || "—"}</td>
                    <td>{email || "—"}</td>
                    <td>{loc || "—"}</td>
                    <td>{status || "—"}</td>
                    <td>{micro || "—"}</td>
                    <td>{installed || "—"}</td>
                    <td style={{ display: "flex", gap: 10, flexWrap: "wrap" }}>
                      <button
                        type="button"
                        className="secondary-button"
                        onClick={() => goToReadings(id)}
                        disabled={!id}
                      >
                        Readings
                      </button>

                      <button
                        type="button"
                        className="secondary-button"
                        onClick={() => openEdit(m)}
                      >
                        Edit
                      </button>

                      <button
                        type="button"
                        className="danger-button"
                        onClick={() => setConfirmDeleteId(id)}
                        disabled={!id}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                );
              })
            )}
          </tbody>
        </table>
      </div>

      {/* EDIT MODAL - uses same form-group structure */}
      {editOpen && (
        <div className="modal-backdrop modal-fade" role="dialog" aria-modal="true"
             onMouseDown={closeEdit}>
          <div className="modal modal-pop" onMouseDown={(e) => e.stopPropagation()}>
            <h3 className="modal-title">Edit Monitor</h3>
            <p className="modal-text">
              Update monitor/building details. Monitor ID is locked.
            </p>

            <form className="login-form" onSubmit={saveEdit}>
              <div className="form-group">
                <label htmlFor="edit-monitor-id">Monitor ID</label>
                <input id="edit-monitor-id" value={editId} disabled readOnly />
              </div>

              <div className="form-group">
                <label htmlFor="edit-user-email">Owner Email</label>
                <input
                  id="edit-user-email"
                  type="text"
                  value={editUserEmail}
                  onChange={(e) => setEditUserEmail(e.target.value)}
                  placeholder="owner@email.com"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="edit-location">Location (Building)</label>
                <input
                  id="edit-location"
                  type="text"
                  value={editLocation}
                  onChange={(e) => setEditLocation(e.target.value)}
                  placeholder="Building / address / site name"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="edit-status">Status</label>
                <select
                  id="edit-status"
                  value={editStatus}
                  onChange={(e) => setEditStatus(e.target.value)}
                >
                  <option value="online">online</option>
                  <option value="offline">offline</option>
                  <option value="maintenance">maintenance</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="edit-micro">Microprocessor Type</label>
                <input
                  id="edit-micro"
                  type="text"
                  value={editMicro}
                  onChange={(e) => setEditMicro(e.target.value)}
                  placeholder="e.g., ESP32"
                />
              </div>

              <div className="form-group">
                <label htmlFor="edit-install-date">Installation Date</label>
                <input
                  id="edit-install-date"
                  type="date"
                  value={editInstallDate}
                  onChange={(e) => setEditInstallDate(e.target.value)}
                />
              </div>

              <div className="modal-actions">
                <button type="submit" className="cta-button" disabled={saving}>
                  {saving ? "Saving..." : "Save changes"}
                </button>
                <button type="button" className="secondary-button" onClick={closeEdit} disabled={saving}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* DELETE CONFIRM MODAL */}
      {confirmDeleteId && (
        <div
          className="modal-backdrop modal-fade"
          role="dialog"
          aria-modal="true"
          onMouseDown={() => setConfirmDeleteId(null)}
        >
          <div className="modal modal-pop" onMouseDown={(e) => e.stopPropagation()}>
            <h3 className="modal-title">Delete monitor?</h3>
            <p className="modal-text">
              Are you sure you want to delete monitor <b>{confirmDeleteId}</b>?
              <br />
              This action cannot be undone.
            </p>

            <div className="modal-actions">
              <button
                type="button"
                className="danger-button"
                onClick={() => deleteMonitor(confirmDeleteId)}
              >
                Yes, delete
              </button>
              <button
                type="button"
                className="secondary-button"
                onClick={() => setConfirmDeleteId(null)}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </section>
  );
}
