import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:5000"
});

export function formatDateTime(value) {
  if (!value) return "";

  const d = new Date(value);

  // Example: 01 Jan 2026, 19:19
  return d.toLocaleString("en-GB", {
    day: "2-digit",
    month: "short",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
    hour12: false,
  });
}


export default api;
