// src/components/analytics/NiceBars.js
import React, { useMemo } from "react";

function safeNum(v) {
  const n = Number(v);
  return Number.isFinite(n) ? n : 0;
}

export default function BarTimeChart({
  title,
  subtitle,
  items = [], // [{ label, value, hint }]
}) {
  const prepared = useMemo(() => {
    const vals = items.map((it) => safeNum(it.value));
    const max = Math.max(1, ...vals);
    return items.map((it) => ({
      label: it.label,
      value: safeNum(it.value),
      hint: it.hint || "",
      pct: Math.round((safeNum(it.value) / max) * 100),
    }));
  }, [items]);

  return (
    <div className="chart-card">
      <div className="chart-header">
        <h3 className="chart-title">{title}</h3>
        {subtitle ? <div className="muted" style={{ fontSize: 12 }}>{subtitle}</div> : null}
      </div>

      <div className="chart-container">
        <div className="bar-chart" style={{ display: "flex", gap: 12, alignItems: "flex-end", height: 170 }}>
          {prepared.map((b) => (
            <div key={b.label} className="bar" style={{ flex: 1, display: "flex", flexDirection: "column", justifyContent: "flex-end" }}>
              <div
                className="bar-fill"
                style={{
                  height: `${b.pct}%`,
                  borderRadius: 12,
                  background: "rgba(255,255,255,0.14)",
                  border: "1px solid rgba(255,255,255,0.14)",
                  transition: "height 400ms ease",
                }}
                title={`${b.value}`}
              />
              <span className="bar-label" style={{ marginTop: 10, fontSize: 12, opacity: 0.8 }}>
                {b.label}
              </span>
              {b.hint ? (
                <span style={{ fontSize: 11, opacity: 0.6, marginTop: 4 }}>
                  {b.hint}
                </span>
              ) : null}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
